mapper.select:
select gj.objectid,gj.xtbh,gj.zymc,gj.gjjxbh,gj.ymc,gj.gdzcbh, gj.sccj,gj.ggxh,gj.bcrl,gj.ythbz,gj.splx,gj.ywjb,gj.jtwz,gj.dlmc, gj.mp,gj.azfs,gj.khplfs,gj.klplfs,gj.odmkhs,gj.odmkls,gj.wyzt,gj.cqxz,gj.sszt, gj.whfs,gj.yt,gj.ssgc,gj.zjdw,gj.cjdw,gj.ssglq,gj.regionid, round(gj.lon,6) lon,round(gj.lat,6) lat,gj.orgid,gj.status,gj.ewm from RES_GJJX gj left join res_gprs_record r on gj.xtbh = r.resid and r.restype ='AA003' where 1=1 and ( gj.status is null or gj.status != '9' )
and r.simid like '%' || #{simid,jdbcType=VARCHAR} || '%'
and gj.zymc like '%' || #{zymc,jdbcType=VARCHAR} || '%'
and gj.SSZT = #{sszt,jdbcType=VARCHAR}
and gj.CQXZ = #{cqxz,jdbcType=VARCHAR}
and gj.regionid in
#{regionid}
and gj.createdate >= to_date(#{starttime,jdbcType=VARCHAR},'yyyy/mm/dd HH24:mi:ss')
and gj.createdate <= to_date(#{endtime,jdbcType=VARCHAR},'yyyy/mm/dd HH24:mi:ss')
order by gj.createdate desc

select gj.objectid,gj.xtbh,gj.zymc,gj.gjjxbh,gj.ymc,gj.gdzcbh, gj.sccj,gj.ggxh,gj.bcrl,gj.ythbz,gj.splx,gj.ywjb,gj.jtwz,gj.dlmc, gj.mp,gj.azfs,gj.khplfs,gj.klplfs,gj.odmkhs,gj.odmkls,gj.wyzt,gj.cqxz,gj.sszt, gj.whfs,gj.yt,gj.ssgc,gj.zjdw,gj.cjdw,gj.ssglq,gj.regionid, round(gj.lon,6) lon,round(gj.lat,6) lat,gj.orgid,gj.status,gj.ewm from RES_GJJX gj where ( status is null or status != '9' )
and zymc like '%' || #{zymc,jdbcType=VARCHAR} || '%'
and SSZT = #{sszt,jdbcType=VARCHAR}
and CQXZ = #{cqxz,jdbcType=VARCHAR}
and regionid in
#{regionid}
and createdate >= to_date(#{starttime,jdbcType=VARCHAR},'yyyy/mm/dd HH24:mi:ss')
and createdate <= to_date(#{endtime,jdbcType=VARCHAR},'yyyy/mm/dd HH24:mi:ss')
order by createdate desc
