mapper.select:
select max(height) from (
select decode(bplfs,'AD001',dybs*22,'AD006',dybs*22,'AD004',bdzs*18,'AD005',bdzs*18,'') height from res_odm where gh in 

	#{kh}

and sssb=#{sssb,jdbcType=VARCHAR} and sssbm=#{sssbm,jdbcType=VARCHAR} and (status is null or status != '9')
)

select max(width) from (
select decode(bplfs,'AD001',bdzs*18,'AD006',bdzs*18,'AD004',dybs*22,'AD005',dybs*22,'') width from res_odm where gh in 

	#{kh}

and sssb=#{sssb,jdbcType=VARCHAR} and sssbm=#{sssbm,jdbcType=VARCHAR} and (status is null or status != '9')
)

select.if:
select distinct to_firstunit lines from (
	select case when from_id = #{xtbh,jdbcType=VARCHAR} then from_firstunit when to_id = #{xtbh,jdbcType=VARCHAR} then to_firstunit else '-1' end to_firstunit from res_sb_conn where connector_id = #{topid,jdbcType=VARCHAR} and connector_type=#{toptype,jdbcType=VARCHAR} and (status is null or status != '9')
)
