mapper.select:
select t.XTBH,d.zymc ZDBHD, g.regionname REGIONNAME, t.ZYMC, t.JFBM, t.YMC, t.REGIONID, d.zymc sszd, FN_GETNAMEBYCODE(t.JFLX, 'JFLX') JFLX_NAME, t.JFLX, t.SZJZW, t.LCH, t.FJH, FN_GETNAMEBYCODE(t.CQXZ, 'property_right') CQXZ, to_char(t.CJSJ, 'yyyy-MM-dd HH24:mi:ss') CJSJ, t.SX, t.BM, t.CD, t.GD, t.KD, t.GDZCBH, t.BZ, t.COUNTYID, FN_GETNAMEBYCODE(t.YWJB, 'CABLETYPE') YWJB, t.PRECINCT, t.STATUS, t.WHFS, t.EWM, o.ORGNAME orgname, p.NAME PATROLGROUP from res_jf t left join res_zdxx d on t.sszd = d.xtbh left join res_maintenance m on d.xtbh = m.rs_id and m.rs_type = 'C31' left join view_org o on m.maintenance_id = o.ID left join view_patrolgroup p on p.ID = m.patrol_group_id left join view_region g on t.regionid = g.regionid where 1 = 1 and (t.status is null or t.status != '9')
and t.jflx=#{jflx,jdbcType=VARCHAR}
and t.cqxz=#{cqxz,jdbcType=VARCHAR}
and t.zymc like '%' || #{zymc,jdbcType=VARCHAR} || '%'
and t.jfbm like '%' || #{jfbm,jdbcType=VARCHAR} || '%'
and o.ID = #{orgid,jdbcType=VARCHAR}
and t.precinct like '%' || #{precinct,jdbcType=VARCHAR}||'%'
and to_char(t.cjsj,'yyyy-mm-dd HH24:mi:ss') >= #{starttime,jdbcType=VARCHAR}
and to_char(t.cjsj,'yyyy-mm-dd HH24:mi:ss') <= #{endtime,jdbcType=VARCHAR}
and t.regionid in
#{regionid,jdbcType=VARCHAR}
and m.maintenance_id in
#{orgid,jdbcType=VARCHAR}
and m.rs_id=n.site_id and m.rs_type='C31'
and m.patrol_group_id = #{patrolgroupid,jdbcType=VARCHAR}
ORDER BY t.cjsj desc

select t.XTBH,d.xtbh zdbh, g.regionname REGIONNAME, t.ZYMC, t.JFBM, t.YMC, t.REGIONID, d.zymc sszd, FN_GETNAMEBYCODE(t.JFLX, 'JFLX') JFLX_NAME, t.JFLX, t.SZJZW, t.LCH, t.FJH, FN_GETNAMEBYCODE(t.CQXZ, 'property_right') CQXZ, to_char(t.CJSJ, 'yyyy-MM-dd HH24:mi:ss') CJSJ, t.SX, t.BM, t.CD, t.GD, t.KD, t.GDZCBH, t.BZ, t.COUNTYID, FN_GETNAMEBYCODE(t.YWJB, 'CABLETYPE') YWJB, t.PRECINCT, t.STATUS, t.WHFS, t.EWM, o.ORGNAME from res_jf t left join res_zdxx d on t.sszd = d.xtbh left join res_maintenance m on d.xtbh = m.rs_id and m.rs_type = 'C31' left join view_org o on m.maintenance_id = o.ID left join view_patrolgroup p on p.ID = m.patrol_group_id left join view_region g on d.regionid = g.regionid where 1 = 1 and (t.status is null or t.status != '9')
and t.jflx=#{jflx,jdbcType=VARCHAR}
and t.cqxz=#{cqxz,jdbcType=VARCHAR}
and t.zymc like '%' || #{zymc,jdbcType=VARCHAR} || '%'
and t.jfbm like '%' || #{jfbm,jdbcType=VARCHAR} || '%'
and t.precinct like '%' || #{precinct,jdbcType=VARCHAR}||'%'
and to_char(t.cjsj,'yyyy-mm-dd HH24:mi:ss') >= #{starttime,jdbcType=VARCHAR}
and to_char(t.cjsj,'yyyy-mm-dd HH24:mi:ss') <= #{endtime,jdbcType=VARCHAR}
and d.regionid in
#{regionid,jdbcType=VARCHAR}
and m.maintenance_id in
#{orgid,jdbcType=VARCHAR}
and m.rs_id=n.site_id and m.rs_type='C31'
and m.patrol_group_id = #{patrolgroupid,jdbcType=VARCHAR}
and exists (select 1 from res_maintenance r where r.maintenance_id in
#{orgid,jdbcType=VARCHAR}
and r.rs_id=xtbh)
ORDER BY cjsj desc

select s.xtbh, r.regionname REGIONNAME, s.zymc, s.jfbm, s.ymc, s.regionid, z.zymc sszd, FN_GETNAMEBYCODE(s.jflx, 'JFLX') JFLX_NAME, s.jflx, s.szjzw, s.lch, s.fjh, FN_GETNAMEBYCODE(s.cqxz, 'property_right') CQXZ, to_char(s.cjsj, 'yyyy-MM-dd HH24:mi:ss') CJSJ, s.sx, s.bm, s.cd, s.gd, s.kd, s.gdzcbh, s.bz, s.countyid, s.ywjb, FN_GETNAMEBYCODE(s.ywjb, 'CABLETYPE') ywjb_name, s.precinct, s.whfs, s.ewm, o.ORGNAME from res_jf s left join res_zdxx z on s.sszd = z.xtbh left join view_region r on r.regionid=s.regionid where (s.status is null or s.status != '9')
and s.jflx=#{jflx,jdbcType=VARCHAR}
and s.cqxz=#{cqxz,jdbcType=VARCHAR}
and s.regionid in
#{regionid,jdbcType=VARCHAR}
and s.zymc like '%' || #{zymc,jdbcType=VARCHAR} || '%'
and s.jfbm like '%' || #{jfbm,jdbcType=VARCHAR} || '%'
and s.precinct like '%' || #{precinct,jdbcType=VARCHAR} || '%'
and to_char(s.cjsj,'yyyy-mm-dd HH24:mi:ss') >= #{starttime,jdbcType=VARCHAR}
and to_char(s.cjsj,'yyyy-mm-dd HH24:mi:ss') <= #{endtime,jdbcType=VARCHAR}
ORDER BY s.xtbh desc

select d.*,r.deviceid from res_jf d,res_gprs_record r
where
r.resid=d.xtbh and r.restype='AD701' and ( d.status is null or
d.status != '9' )

	and r.simid = #{simid,jdbcType=VARCHAR}


	and d.jflx=#{jflx,jdbcType=VARCHAR}


	and d.cqxz=#{cqxz,jdbcType=VARCHAR}


	and d.regionid in

		#{regionid}



	and d.zymc like '%' || #{zymc,jdbcType=VARCHAR} || '%'


	and d.jfbm like '%' || #{jfbm,jdbcType=VARCHAR} || '%'


	and d.precinct like '%' || #{precinct,jdbcType=VARCHAR} ||
	'%'



	and to_char(d.cjsj,'yyyy-mm-dd HH24:mi:ss') >= #{starttime,jdbcType=VARCHAR}




	and to_char(d.cjsj,'yyyy-mm-dd HH24:mi:ss') <= #{endtime,jdbcType=VARCHAR}


ORDER BY d.cjsj desc

